sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
    //g_form.setDisplay('u_bonus_manager', false);
	var u_manager = g_form.getBooleanValue('u_manager');

	if (u_manager === true)
	{
		var bonusAmount = (g_form.getDecimalValue('u_salary') * 10) / 100;
		//g_form.setDisplay('u_bonus_manager', true);
		g_form.setValue('u_bonus_manager', bonusAmount);

	}   
}
