sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	// 1. Create an object to store rows from a table
	var employees = new GlideRecord('x_479478_employee_employee');
	var leaves = new GlideRecord("x_479478_employee_leaves");

	// 2. Build query
	employees.addQuery('u_name','=', current.u_name);
	leaves.addQuery('u_state', '=', 'approved');
	leaves.addQuery('u_requested_by_ref', '=', current.sys_id);

	// 3. Execute query 
	employees.query();
	leaves.query();

	var leaveTaken = 0;
	// 4. Process returned records

	if (employees.next())
	{
		while (leaves.next())
		{
			leaveTaken += parseInt(employees.u_leave_taken) + 1;
		}
		employees.setValue('u_leave_taken', leaveTaken); //employees.getValue('u_leave_taken') + 1);

		employees.update();
	}
	current.u_leave_taken = leaveTaken;

})(current, previous);
