sys_ui_action.script:
(function executeRule(current /*null when async*/) {



	// Add your code here
	// 1. Create an object to store rows from a table
	var employees = new GlideRecord('x_479478_employee_employee');

	// 2. Build query
	employees.addQuery('u_name','=', current.u_name);

	// 3. Execute query 
	employees.query();

	// 4. Process returned records
	if(employees.next()){
		//Logic you want to execute. 
		var leaves = new GlideRecord("x_479478_employee_leaves");

		leaves.initialize();
		leaves.u_state = 'Requested';
		leaves.u_requested_by_ref = current.sys_id;
		leaves.u_assigned_to_ref = current.u_reference_8;


		leaves.insert();

		var managerEmail = current.u_reference_8.email;
		gs.info("Email has been sent to:" + managerEmail);

	}

})(current);
