sysauto_script.script:
/*
4.	A scheduled script should run for every 10 minutes and send a notification 
via email if any employee records were inserted / updated with salary more than 50,000. Server-Side 
*/

// 1. Create an object to store rows from a table
var employees = new GlideRecord('x_479478_employee_employee');

// 2. Build query
employees.addQuery('u_salary','>', 50000);

// 3. Execute query 
employees.query();

// Write in log message
while(employees.next()){
	//gs.eventQueue('send.email.notification', employees, null, null);
	gs.info(employees.u_name + " has $" + employees.u_salary + " salary which is > $50,000" );
}
